async function request(endpoint, options = {}) {
    const token = localStorage.getItem("token");
    const headers = {
        "Content-Type": "application/json",
        ...token ? {
            Authorization: `Bearer ${token}`
        } : {}
    };
    const res = await fetch(`${API_URL}${endpoint}`, {
        ...options,
        headers
    });
    if (!res.ok) {
        throw new Error("Ошибка API");
    }
    return res.json();
}
